#' Violin Plot
#'
#' 
#' @param data .
#' @param dep .
#' @param group .
#' @param col .
#' @param fill .
#' @param excl .
#' @param flip .
#' @param themex .
#' @param usexlabel .
#' @param xlabel .
#' @param useylabel .
#' @param ylabel .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
jviolin <- function(
    data,
    dep,
    group,
    col,
    fill,
    excl = FALSE,
    flip = FALSE,
    themex = "ipsum",
    usexlabel = FALSE,
    xlabel = "",
    useylabel = FALSE,
    ylabel = "") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("jviolin requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if ( ! missing(col)) col <- jmvcore::resolveQuo(jmvcore::enquo(col))
    if ( ! missing(fill)) fill <- jmvcore::resolveQuo(jmvcore::enquo(fill))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL),
            `if`( ! missing(col), col, NULL),
            `if`( ! missing(fill), fill, NULL))


    options <- jviolinOptions$new(
        dep = dep,
        group = group,
        col = col,
        fill = fill,
        excl = excl,
        flip = flip,
        themex = themex,
        usexlabel = usexlabel,
        xlabel = xlabel,
        useylabel = useylabel,
        ylabel = ylabel)

    analysis <- jviolinClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

