#' ROC
#'
#' Function for ROC Analysis.
#'
#' @examples
#' \donttest{
#' # example will be added
#'}
#' @param data The data as a data frame.
#' @param measurement .
#' @param status .
#' @param excl .
#' @param sty .
#' @param quant .
#' @param label .
#' @param inter .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$textplot} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
roc2 <- function(
    data,
    measurement,
    status,
    excl = FALSE,
    sty = FALSE,
    quant = FALSE,
    label = FALSE,
    inter = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("roc2 requires jmvcore to be installed (restart may be required)")

    if ( ! missing(measurement)) measurement <- jmvcore::resolveQuo(jmvcore::enquo(measurement))
    if ( ! missing(status)) status <- jmvcore::resolveQuo(jmvcore::enquo(status))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(measurement), measurement, NULL),
            `if`( ! missing(status), status, NULL))


    options <- roc2Options$new(
        measurement = measurement,
        status = status,
        excl = excl,
        sty = sty,
        quant = quant,
        label = label,
        inter = inter)

    analysis <- roc2Class$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

