#' Stats Plot Between
#'
#' 
#' @param data .
#' @param dep .
#' @param group .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plothtml} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
statsplotbetween <- function(
    data,
    dep,
    group) {

    if ( ! requireNamespace('jmvcore'))
        stop('statsplotbetween requires jmvcore to be installed (restart may be required)')

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(group), group, NULL))


    options <- statsplotbetweenOptions$new(
        dep = dep,
        group = group)

    analysis <- statsplotbetweenClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
